import timeprint('-'*70)print('                            Mass Converter')def main():        print('-'*70)        time.sleep(2)        userNumber = float(input("Please input the amount of grams(g) "+                             "you would like to convert:"))        print('-'*70)        print('Processing...')        userKilogram = round((userNumber/1000),5)        userMilligram = round((userNumber*1000),5)        userTons = round((userNumber/1e+6),6)        userPounds = round((userNumber/454),5)        userStones = round((userNumber/6350),5)        print('-'*70)        time.sleep(2)        print('Results:\n')        print('Kilograms: {}'.format(userKilogram))        print('Milligrams: {}'.format(userMilligram))        print('Tons: {}'.format(userTons))        print('Pounds: {}'.format(userPounds))        print('Stones: {}'.format(userStones))        print('-'*70)        while True:        main()    time.sleep(2)        if str(input("Would you like to convert again?(Y/N)")).strip().upper() != "Y":              print("\nGoodbye!\n")            time.sleep(1)            break